import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [imageIndex, setImageIndex] = useState(0);
  const [cartCount, setCartCount] = useState(0);

  const toggleImage = () => {
    setImageIndex((prevIndex) => (prevIndex === 0 ? 1 : 0));
  };

  const incrementCartCount = () => {
    setCartCount((prevCount) => prevCount + 1);
  };

  const decrementCartCount = () => {
    if (cartCount > 0) {
      setCartCount((prevCount) => prevCount - 1);
    }
  };

  return (
    <div className="App">
      <header>
        <img src="logo.png" alt="Logo" />
        <nav>
          <ul>
            <li>Главная</li>
            <li>Товары</li>
          </ul>
        </nav>
      </header>
      <div className="content">
        <h1>Мама, я не хочу умирать</h1>
        <div className="image-container" onClick={toggleImage}>
          <img
            src={imageIndex === 0 ? 'image1.jpg' : 'image2.jpg'}
            alt="Main Image"
          />
        </div>
        <h2>Мама, я не хочу умирать</h2>
      </div>
      <div className="products">
        <div className="product-card">
          <img src="product1.jpg" alt="Product 1" />
          <h3>Название товара 1</h3>
          <p>Количество: 0</p>
          <button onClick={incrementCartCount}>+</button>
          <button onClick={decrementCartCount}>-</button>
        </div>
        <div className="product-card">
          <img src="product2.jpg" alt="Product 2" />
          <h3>Название товара 2</h3>
          <p>Количество: 0</p>
          <button onClick={incrementCartCount}>+</button>
          <button onClick={decrementCartCount}>-</button>
        </div>
        <div className="product-card">
          <img src="product3.jpg" alt="Product 3" />
          <h3>Название товара 3</h3>
          <p>Количество: 0</p>
          <button onClick={incrementCartCount}>+</button>
          <button onClick={decrementCartCount}>-</button>
        </div>
      </div>
      <div className="cart">
        <img src="cart-icon.png" alt="Cart Icon" />
        <span className="cart-count">{cartCount}</span>
      </div>
    </div>
  );
};

export default App;
