import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [imageIndex, setImageIndex] = useState(0);
  const [cartCount, setCartCount] = useState(0);
  const [currentPage, setCurrentPage] = useState('home');
  const [totalQuantity, setTotalQuantity] = useState(0); // Счетчик суммы выбранных товаров

  const toggleImage = () => {
    setImageIndex((prevIndex) => (prevIndex === 0 ? 1 : 0));
  };

  const incrementCartCount = () => {
    setCartCount((prevCount) => prevCount + 1);
  };

  const decrementCartCount = () => {
    if (cartCount > 0) {
      setCartCount((prevCount) => prevCount - 1);
    }
  };

  const handleMenuClick = (page) => {
    setCurrentPage(page);
  };

  const [products, setProducts] = useState([
    { id: 1, name: 'Название товара 1', quantity: 0 },
    { id: 2, name: 'Название товара 2', quantity: 0 },
    { id: 3, name: 'Название товара 3', quantity: 0 },
  ]);

  const incrementProductQuantity = (productId) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId
          ? { ...product, quantity: product.quantity + 1 }
          : product
      )
    );
  };

  const decrementProductQuantity = (productId) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId && product.quantity > 0
          ? { ...product, quantity: product.quantity - 1 }
          : product
      )
    );
  };

  // Вычисление суммы выбранных товаров
  useState(() => {
    let sum = 0;
    products.forEach((product) => {
      sum += product.quantity;
    });
    setTotalQuantity(sum);
  }, [products]);

  const renderPage = () => {
    if (currentPage === 'home') {
      return (
        <div className="content">
          <h1>Мама, я не хочу умирать</h1>
          <div className="image-container" onClick={toggleImage}>
            <img
              src={imageIndex === 0 ? process.env.PUBLIC_URL + '/images/image1.jpg' : process.env.PUBLIC_URL + '/images/image2.jpg'}
              alt="Main Image"
            />
          </div>
          <h2>Мама, я не хочу умирать</h2>
        </div>
      );
    } else if (currentPage === 'products') {
      return (
        <div className="products">
          {products.map((product) => (
            <div className="product-card" key={product.id}>
              <img src={`product${product.id}.jpg`} alt={product.name} />
              <h3>{product.name}</h3>
              <p>Количество: {product.quantity}</p>
              <button onClick={() => incrementProductQuantity(product.id)}>+</button>
              <button onClick={() => decrementProductQuantity(product.id)}>-</button>
            </div>
          ))}
        </div>
      );
    }
  };

  return (
    <div className="App">
      <header>
        <img src={process.env.PUBLIC_URL + '/images/logo.png'} alt="Logo" className="logo-image" />
        <nav>
          <ul>
            <li onClick={() => handleMenuClick('home')}>Главная</li>
            <li onClick={() => handleMenuClick('products')}>Товары</li>
          </ul>
        </nav>
        <div className="cart">
          <img src="cart-icon.png" alt="Cart Icon" />
          <span className="cart-count">{totalQuantity}</span> {/* Отображение суммы выбранных товаров */}
        </div>
      </header>
      {renderPage()}
    </div>
  );
};

export default App;
